import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { User } from './user.model';

@Component({
    selector: 'app-digit-complete-template-driven-validation-decorators',
    templateUrl: './digit-complete.component.html'
})
export class DigitCompleteTemplateDrivenValidationDecoratorsComponent implements OnInit {
    user: User
	
    constructor(
    ) { }

    ngOnInit() {
       this.user= new User()
    }
}

complete.model:
import {  digit, } from   "@rxweb/reactive-form-validators"   
 
export class User {

	@digit() 
	age: number;

	//If you want to apply conditional expression of type 'function'
	@digit({conditionalExpression:(x,y) => x.age >= 25  }) 
	phoneNumber: number;

	//If you want to apply conditional expression of type 'string'
	@digit({conditionalExpression:'x => x.age >=25' }) 
	faxNumber: number;

	@digit({message:'Please enter only digit.' }) 
	mobileNumber: number;

}

