import { FormGroup,Validators } from "@angular/forms"
import { User } from './user.model';

@Component({
    selector: 'app-greaterThan-complete-template-driven-validation-decorators',
    templateUrl: './greater-than-complete.component.html'
})
export class GreaterThanCompleteTemplateDrivenValidationDecoratorsComponent implements OnInit {
    user: User
	
    constructor(
    ) { }

    ngOnInit() {
       this.user= new User()
    }
}

complete.model:
import {  greaterThan,prop, } from   "@rxweb/reactive-form-validators"   

export class User {

	@prop()
	age: number;

	@greaterThan({fieldName:'age' }) 
	retiermentAge: number;
	
	
	

	//If you want to apply conditional expression of type 'function'
	@greaterThan({fieldName:'age'  ,conditionalExpression:(x,y) => x.age > 17  }) 
	memberAge: number;
	
	
	

	//If you want to apply conditional expression of type 'string'
	@greaterThan({fieldName:'age'  ,conditionalExpression:'x => x.age > 17' }) 
	voterAge: number;
	
	
	

	@greaterThan({fieldName:'age'  ,message:'Please enter number greater than 0.' }) 
	otherAge: number;
	
	
	

}

