complete.component:
import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { Country } from './country.model';

@Component({
    selector: 'app-latLong-complete-template-driven-validation-directives',
    templateUrl: './lat-long-complete.component.html'
})
export class LatLongCompleteTemplateDrivenValidationDirectivesComponent implements OnInit {
    country: Country
	
    constructor(
    ) { }

    ngOnInit() {
       this.country= new Country()
    }
}

complete.model:
export class Country {

	continent: string;

	secondCountry: string;

	thirdCountry: string;

	firstCountry: string;

}

