import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { Country } from './country.model';

@Component({
    selector: 'app-latitude-complete-template-driven-validation-decorators',
    templateUrl: './latitude-complete.component.html'
})
export class LatitudeCompleteTemplateDrivenValidationDecoratorsComponent implements OnInit {
    country: Country
	
    constructor(
    ) { }

    ngOnInit() {
       this.country= new Country()
    }
}

complete.model:
import {  latitude,prop, } from "@rxweb/reactive-form-validators"

export class Country {

	@prop()
	continent: string;

	//If you want to apply conditional expression of type 'function'
	@latitude({conditionalExpression:(x,y) => x.continent == "Asia"  }) 
	secondCountryLatitude: string;

	//If you want to apply conditional expression of type 'string'
	@latitude({conditionalExpression:'x => x.continent =="Asia"' }) 
	thirdCountryLatitude: string;

	@latitude({message:'{{0}} is not a latitude' }) 
	firstCountryLatitude: string;

}

