complete.component:
import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { Country } from './country.model';

@Component({
    selector: 'app-longitude-complete-template-driven-validation-directives',
    templateUrl: './longitude-complete.component.html'
})
export class LongitudeCompleteTemplateDrivenValidationDirectivesComponent implements OnInit {
    country: Country
	
    constructor(
    ) { }

    ngOnInit() {
       this.country= new Country()
    }
}

complete.model:
export class Country {

	continent: string;

	secondCountryLongitude: string;

	thirdCountryLongitude: string;

	firstCountryLongitude: string;

}

