complete.component:
import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { User } from './user.model';

@Component({
    selector: 'app-lowerCase-complete-template-driven-validation-directives',
    templateUrl: './lower-case-complete.component.html'
})
export class LowerCaseCompleteTemplateDrivenValidationDirectivesComponent implements OnInit {
    user: User
	
    constructor(
    ) { }

    ngOnInit() {
       this.user= new User()
    }
}

complete.model:
export class User {

	username: string;

	firstName: string;

	middleName: string;

	lastName: string;

}

