complete.component:
import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { User } from './user.model';

@Component({
    selector: 'app-maxDate-complete-template-driven-validation-directives',
    templateUrl: './max-date-complete.component.html'
})
export class MaxDateCompleteTemplateDrivenValidationDirectivesComponent implements OnInit {
    user: User
	
    constructor(
    ) { }

    ngOnInit() {
       this.user= new User()
    }
}

complete.model:
export class User {

	userName: string;

	allocationDate: Date;

	birthDate: Date;

