import { FormGroup,Validators } from "@angular/forms"
import { User } from './user.model';

@Component({
    selector: 'app-maxLength-complete-template-driven-validation-decorators',
    templateUrl: './max-length-complete.component.html'
})
export class MaxLengthCompleteTemplateDrivenValidationDecoratorsComponent implements OnInit {
    user: User
	
    constructor(
    ) { }

    ngOnInit() {
       this.user= new User()
    }
}

complete.model:
import {  maxLength, } from "@rxweb/reactive-form-validators"

export class User {

	@maxLength({value:16 }) 
	firstName: string;

	//If you want to apply conditional expression of type 'function'
	@maxLength({value:16  ,conditionalExpression:(x,y)=> x.firstName == "Bharat" }) 
	middleName: string;

	//If you want to apply conditional expression of type 'string'
	@maxLength({value:16  ,conditionalExpression:'x => x.firstName == "Bharat"' }) 
	lastName: string;

	//Shows custom message
	@maxLength({value:10  ,message:'Maximum 10 characters are allowed' }) 
	userName: string;

}

