@Component({
    selector: 'app-minDate-complete-template-driven-validation-decorators',
    templateUrl: './min-date-complete.component.html'
})
export class MinDateCompleteTemplateDrivenValidationDecoratorsComponent implements OnInit {
    user: User
	
