import { FormGroup,Validators } from "@angular/forms"
import { Contact } from './contact.model';

@Component({
    selector: 'app-minLength-complete-template-driven-validation-decorators',
    templateUrl: './min-length-complete.component.html'
})
export class MinLengthCompleteTemplateDrivenValidationDecoratorsComponent implements OnInit {
    contact: Contact
	
    constructor(
    ) { }

    ngOnInit() {
       this.contact= new Contact()
    }
}

complete.model:
import {  minLength,prop, } from "@rxweb/reactive-form-validators"

export class Contact {

	@prop()
	countryName: string;

	@minLength({value:10 }) 
	mobileNo: string;

	@minLength({value:8  ,message:'Minimum 8 characters are allowed' }) 
	landLineNo: string;

	//If you want to apply conditional expression of type 'function'
	@minLength({value:3  ,conditionalExpression:(x,y)=> x.countryName == "India" }) 
	countryCode: string;

	//If you want to apply conditional expression of type 'string'
	@minLength({value:3  ,conditionalExpression:'x => x.countryName == "India"' }) 
	stateCode: string;

}

