import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { ResultInfo } from './result-info.model';

@Component({
    selector: 'app-minNumber-complete-template-driven-validation-decorators',
    templateUrl: './min-number-complete.component.html'
})
export class MinNumberCompleteTemplateDrivenValidationDecoratorsComponent implements OnInit {
    resultinfo: ResultInfo
	
    constructor(
    ) { }

    ngOnInit() {
       this.resultinfo= new ResultInfo()
    }
}

complete.model:
import {  minNumber, } from   "@rxweb/reactive-form-validators"   

export class ResultInfo {

	@minNumber({value:35 }) 
	maths: number;
	
	
	@minNumber({value:35  ,message:'Number should not be less than 35' }) 
	science: number;
	
	
	//If you want to apply conditional expression of type 'function'
	@minNumber({value:35  ,conditionalExpression:(x,y) => x.maths == 50  }) 
	english: number;
	
	
	@minNumber({value:35  ,conditionalExpression:'x => x.maths == 50' }) 
	statstics: number;
	
	
}

