complete.component:
import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { User } from './user.model';

@Component({
    selector: 'app-pattern-complete-template-driven-validation-directives',
    templateUrl: './pattern-complete.component.html'
})
export class PatternCompleteTemplateDrivenValidationDirectivesComponent implements OnInit {
    user: User
	
    constructor(
    ) { }

    ngOnInit() {
       this.user= new User()
    }
}

complete.model:
export class User {

	userName: string;

	zipCode: string;

	contactNumber: number;

	age: string;

}

