import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { NumberInfo } from './number-info.model';

@Component({
    selector: 'app-primeNumber-complete-template-driven-validation-decorators',
    templateUrl: './prime-number-complete.component.html'
})
export class PrimeNumberCompleteTemplateDrivenValidationDecoratorsComponent implements OnInit {
    numberinfo: NumberInfo
	
    constructor(
    ) { }

    ngOnInit() {
       this.numberinfo= new NumberInfo()
    }
}

complete.model:
import {  primeNumber,prop, } from   "@rxweb/reactive-form-validators"   

export class NumberInfo {

	@prop()
	numberType: string;

	//If you want to apply conditional expression of type 'function'
	@primeNumber({conditionalExpression:(x,y) => x.numberType == "Prime"  }) 
	secondNumber: string;
	
	
	

	//If you want to apply conditional expression of type 'string'
	@primeNumber({conditionalExpression:'x => x.numberType =="Prime"' }) 
	thirdNumber: string;
	
	
	

	@primeNumber({message:'{{0}} is not a prime number' }) 
	firstNumber: string;
	
	
	

}

