complete.component:
import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { User } from './user.model';

@Component({
    selector: 'app-required-complete-template-driven-validation-directives',
    templateUrl: './required-complete.component.html'
})
export class RequiredCompleteTemplateDrivenValidationDirectivesComponent implements OnInit {
    user: User
	
    constructor(
    ) { }

    ngOnInit() {
       this.user= new User()
    }
}

complete.model:
export class User {

	firstName: string;

	middleName: string;

	lastName: string;

	userName: string;

}

