complete.component:
import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { User } from './user.model';

@Component({
    selector: 'app-startsWith-complete-template-driven-validation-directives',
    templateUrl: './starts-with-complete.component.html'
})
export class StartsWithCompleteTemplateDrivenValidationDirectivesComponent implements OnInit {
    user: User
	
    constructor(
    ) { }

    ngOnInit() {
       this.user= new User()
    }
}

complete.model:
export class User {

	name: string;

	profession: string;

	taskId: string;

	company: string;

}

