complete.component:
import { Component, OnInit } from '@angular/core';
import { FormGroup,Validators } from "@angular/forms"
import { AttandanceDetail } from './attandance-detail.model';

@Component({
    selector: 'app-time-complete-template-driven-validation-directives',
    templateUrl: './time-complete.component.html'
})
export class TimeCompleteTemplateDrivenValidationDirectivesComponent implements OnInit {
    attandancedetail: AttandanceDetail
	
    constructor(
    ) { }

    ngOnInit() {
       this.attandancedetail= new AttandanceDetail()
    }
}

complete.model:
export class AttandanceDetail {

	entryPlace: string;

	totalIn: string;

	entryTime: string;

	totalOutTime: string;

	exitTime: string;

}

