    selector: 'app-url-complete-template-driven-validation-directives',
    templateUrl: './url-complete.component.html'
})
export class UrlCompleteTemplateDrivenValidationDirectivesComponent implements OnInit {
    user: User
	
    constructor(
    ) { }

    ngOnInit() {
       this.user= new User()
    }
}

